package lc2309

import "testing"

func Test_greatestLetter(t *testing.T) {
	t.Parallel()
	type args struct {
		s string
	}
	tests := []struct {
		name string
		args args
		want string
	}{
		{
			name: `lEeTcOdE -> E`,
			args: args{
				s: `lEeTcOdE`,
			},
			want: `E`,
		},
		{
			name: `arRAzFif -> R`,
			args: args{
				s: `arRAzFif`,
			},
			want: `R`,
		},
		{
			name: `AbCdEfGhIjK -> (empty_string)`,
			args: args{
				s: `AbCdEfGhIjK`,
			},
			want: ``,
		},
		{
			name: `(too_many_A's) -> A`,
			args: args{
				s: ``,
			},
			want: `A`,
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if got := greatestLetter(tt.args.s); got != tt.want {
				t.Errorf("greatestLetter() = %v, want %v", got, tt.want)
			}
		})
	}
}
